import { useEffect, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage, setMessages } from '../store/chatSlice';
import { sendMessage, receiveMessages } from '../services/socketService';
import axios from 'axios';

const Chat = () => {
  const [text, setText] = useState('');
  const messages = useSelector((state) => state.chat.messages);
  const dispatch = useDispatch();

  useEffect(() => {
    axios.get('http://localhost:5000/api/messages') // Fetch chat history
      .then((res) => dispatch(setMessages(res.data)));

    receiveMessages((message) => {
      dispatch(addMessage(message));
    });
  }, [dispatch]);

  const handleSend = () => {
    const message = { text, sender: 'User' }; // Replace with actual user data
    sendMessage(message);
    setText('');
  };

  return (
    <div>
      <div>
        {messages.map((msg, index) => (
          <p key={index}><strong>{msg.sender}:</strong> {msg.text}</p>
        ))}
      </div>
      <input type="text" value={text} onChange={(e) => setText(e.target.value)} />
      <button onClick={handleSend}>Send</button>
    </div>
  );
};

export default Chat;
