import { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../store/chatSlice';
import 'bootstrap/dist/css/bootstrap.min.css';

const Chat = () => {
  const [text, setText] = useState('');
  const messages = useSelector((state) => state.chat.messages);
  const dispatch = useDispatch();
  const chatEndRef = useRef(null);

  useEffect(() => {
    // Auto-scroll to latest message
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (text.trim()) {
      const message = { sender: 'You', text };
      dispatch(addMessage(message));
      setText('');
    }
  };

  return (
    <div className="d-flex flex-column vh-100 bg-light">
      {/* Chat Header */}
      <div className="bg-dark text-white py-3 px-3 d-flex align-items-center">
        <img src="/user-avatar.png" alt="User" className="rounded-circle me-2" width="40" height="40" />
        <h5 className="mb-0">John Doe</h5>
      </div>

      {/* Chat Messages */}
      <div className="flex-grow-1 overflow-auto p-3" style={{ maxHeight: "75vh" }}>
        {messages.length === 0 ? (
          <p className="text-muted text-center mt-5">Say hi to start chatting...</p>
        ) : (
          messages.map((msg, index) => (
            <div 
              key={index} 
              className={`d-flex mb-2 ${msg.sender === 'You' ? 'justify-content-end' : 'justify-content-start'}`}
            >
              <div className={`p-2 rounded-3 ${msg.sender === 'You' ? 'bg-primary text-white' : 'bg-secondary text-white'}`} 
                   style={{ maxWidth: "75%" }}>
                <small className="d-block fw-bold">{msg.sender}</small>
                {msg.text}
              </div>
            </div>
          ))
        )}
        <div ref={chatEndRef} /> {/* Auto-scroll reference */}
      </div>

      {/* Chat Input */}
      <div className="p-3 bg-white border-top d-flex">
        <input 
          type="text" 
          className="form-control me-2" 
          placeholder="Type a message..." 
          value={text} 
          onChange={(e) => setText(e.target.value)} 
        />
        <button className="btn btn-primary" onClick={handleSend}>Send</button>
      </div>
    </div>
  );
};

export default Chat;
