import { useState, useEffect, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addMessage } from '../store/chatSlice';
import 'bootstrap/dist/css/bootstrap.min.css';

const users = [
  { id: 1, name: 'John Doe', avatar: '/user1.png' },
  { id: 2, name: 'Jane Smith', avatar: '/user2.png' },
  { id: 3, name: 'Alice Brown', avatar: '/user3.png' },
  { id: 4, name: 'Bob Johnson', avatar: '/user4.png' }
];

const Chat = () => {
  const [selectedUser, setSelectedUser] = useState(users[0]); // Default chat user
  const [text, setText] = useState('');
  const messages = useSelector((state) => state.chat.messages);
  const dispatch = useDispatch();
  const chatEndRef = useRef(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  const handleSend = () => {
    if (text.trim()) {
      const message = { sender: 'You', text, receiver: selectedUser.name };
      dispatch(addMessage(message));
      setText('');
    }
  };

  return (
    <div className="d-flex vh-100">
      {/* Sidebar */}
      <div className="bg-dark text-white p-3" style={{ width: "250px" }}>
        <h5 className="text-center mb-3">Users</h5>
        <ul className="list-unstyled">
          {users.map((user) => (
            <li 
              key={user.id} 
              className={`p-2 d-flex align-items-center mb-2 rounded ${selectedUser.id === user.id ? 'bg-primary' : ''}`}
              style={{ cursor: 'pointer' }} 
              onClick={() => setSelectedUser(user)}
            >
              <img src={user.avatar} alt={user.name} className="rounded-circle me-2" width="40" height="40" />
              {user.name}
            </li>
          ))}
        </ul>
      </div>

      {/* Chat Section */}
      <div className="d-flex flex-column flex-grow-1 bg-light">
        {/* Chat Header */}
        <div className="bg-primary text-white py-3 px-3 d-flex align-items-center">
          <img src={selectedUser.avatar} alt="User" className="rounded-circle me-2" width="40" height="40" />
          <h5 className="mb-0">{selectedUser.name}</h5>
        </div>

        {/* Chat Messages */}
        <div className="flex-grow-1 overflow-auto p-3" style={{ maxHeight: "75vh" }}>
          {messages.length === 0 ? (
            <p className="text-muted text-center mt-5">Say hi to {selectedUser.name}...</p>
          ) : (
            messages
              .filter((msg) => msg.receiver === selectedUser.name)
              .map((msg, index) => (
                <div 
                  key={index} 
                  className={`d-flex mb-2 ${msg.sender === 'You' ? 'justify-content-end' : 'justify-content-start'}`}
                >
                  <div className={`p-2 rounded-3 ${msg.sender === 'You' ? 'bg-primary text-white' : 'bg-secondary text-white'}`} 
                    style={{ maxWidth: "75%" }}>
                    <small className="d-block fw-bold">{msg.sender}</small>
                    {msg.text}
                  </div>
                </div>
              ))
          )}
          <div ref={chatEndRef} />
        </div>

        {/* Chat Input */}
        <div className="p-3 bg-white border-top d-flex">
          <input 
            type="text" 
            className="form-control me-2" 
            placeholder={`Message ${selectedUser.name}...`} 
            value={text} 
            onChange={(e) => setText(e.target.value)} 
          />
          <button className="btn btn-primary" onClick={handleSend}>Send</button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
